                    {
                        _cc.ExecuteCommand("ALTER DATABASE [" + vDB[i] + "] SET READ_WRITE");
                    }
                    catch
                    {
                    }
                }
                dbInstance = null;
                dbInstanceRate = null;
                VarGeneral.vEndYears = true;
                VarGeneral.DBNo = newDb.FirstOrDefault();
                new FrmMain(null, null, "", 0);
                FrmLog frm = new FrmLog();
                frm.TopMost = true;
                frm.ShowDialog();
                dbInstance = null;
                dbInstanceRate = null;
                FrmMn_Load(null, null);
                return;
            }
            MessageBox.Show((LangArEn == 0) ? "                  .!" : "You are trying to open a database does not contain branches and data possible you bid or the file is incorrect.!", VarGeneral.ProdectNam, MessageBoxButtons.OK, MessageBoxIcon.Question, MessageBoxDefaultButton.Button2, MessageBoxOptions.DefaultDesktopOnly);
        }
