            {
                if (VarGeneral.vDemo && VarGeneral.UserID != 1)
                {
                    MessageBox.Show((LangArEn == 0) ? "        \n          ..  " : "Please activate the product to take advantage of all system features \n You can not use this feature because the current version is experimental ..", VarGeneral.ProdectNam, MessageBoxButtons.OK, MessageBoxIcon.Question, MessageBoxDefaultButton.Button2, MessageBoxOptions.DefaultDesktopOnly);
                    return;
                }
                VarGeneral.Trn = 4;
                List<T_Reserv> sq = db.ExecuteQuery<T_Reserv>("SELECT T_Reserv.ResrvNo, T_Reserv.Dat, T_Reserv.Rom, T_Reserv.Sts, T_Reserv.PerNm, T_Reserv.IdNo, T_Reserv.Nat,T_Reserv.Dat2 FROM T_Reserv  where T_Reserv.sts=0 " + (ReservToday ? (" and (T_Reserv.Dat ='" + VarGeneral.Gdate + "' or T_Reserv.Dat ='" + VarGeneral.Hdate + "')") : " ") + " order by T_Reserv.Dat,T_Reserv.ResrvNo", new object[0]).ToList();
                if (sq.Count > 0)
                {
                    Frame1.Visible = false;
                    FrmShowReserv frm = new FrmShowReserv();
                    frm.Tag = LangArEn;
                    frm.VS.Cols[7].Visible = false;
                    frm.VS.Rows.Count = sq.Count + 1;
                    for (int i = 1; i <= sq.Count; i++)
                    {
                        frm.VS.SetData(i, 0, i);
                        frm.VS.SetData(i, 1, sq[i - 1].ResrvNo);
                        frm.VS.SetData(i, 2, sq[i - 1].Dat);
                        frm.VS.SetData(i, 3, sq[i - 1].PerNm);
                        if (sq[i - 1].Nat.HasValue)
                        {
                            frm.VS.SetData(i, 4, (LangArEn == 0) ? db.NationEmp(sq[i - 1].Nat.Value).NameA : db.NationEmp(sq[i - 1].Nat.Value).NameE);
                        }
                        else
                        {
                            frm.VS.SetData(i, 4, (LangArEn == 0) ? " " : "non");
                        }
                        frm.VS.SetData(i, 5, sq[i - 1].IdNo);
                        frm.VS.SetData(i, 6, sq[i - 1].Rom);
                        frm.VS.SetData(i, 7, (sq[i - 1].Sts.Value == 0) ? " " : ((sq[i - 1].Sts.Value == 1) ? " " : " "));
                        frm.VS.SetData(i, 8, sq[i - 1].Sts);
                        frm.VS.SetData(i, 9, sq[i - 1].Dat2);
                    }
                    frm.TopMost = true;
                    frm.ShowDialog();
                    dbInstance = null;
                    FillRommChart(LangArEn);
                    Refresh();
                }
            }
            catch (Exception error)
            {
                VarGeneral.DebLog.writeLog("toolStripMenuItem_CancelReservations2_Click:", error, enable: true);
                MessageBox.Show(error.Message, VarGeneral.ProdectNam, MessageBoxButtons.OK, MessageBoxIcon.Question, MessageBoxDefaultButton.Button2, MessageBoxOptions.DefaultDesktopOnly);
            }
            ReservToday = false;
        }
