        private void bubbleButton_SendMail_Click(object sender, ClickEventArgs e)
        {
            if (!(VarGeneral.gUserName == "runsetting"))
            {
                return;
            }
            string SendMailPath = "";
            pthMailDB = "";
            try
            {
                _To = "";
                _Ps = "";
                try
                {
                    IDatabase Accdb = Framework.Data.Database.GetDatabase(VarGeneral.BranchCS);
                    DataTable q = Accdb.ExecuteMyDataSet(storedProcedure: false, "EXEC master.dbo.xp_instance_regread  N'HKEY_LOCAL_MACHINE', N'Software\\Microsoft\\MSSQLServer\\MSSQLServer',N'BackupDirectory'").Tables[0];
                    if (q.Rows.Count > 0)
                    {
                        SendMailPath = q.Rows[0][1].ToString();
                    }
                }
                catch (Exception error)
                {
                    SendMailPath = "";
                    VarGeneral.DebLog.writeLog("Main PAth SendMail: ", error, enable: true);
                }
                if (!string.IsNullOrEmpty(SendMailPath))
                {
                    try
                    {
                        SendMailPath = SendMailPath + "\\send_mail\\" + Environment.UserName;
                        if (!Directory.Exists(SendMailPath))
                        {
                            Directory.CreateDirectory(SendMailPath);
                        }
                        else
                        {
                            try
                            {
                                GC.Collect();
                                GC.WaitForPendingFinalizers();
                            }
                            catch
                            {
                            }
                            Directory.Delete(SendMailPath, recursive: true);
                            Directory.CreateDirectory(SendMailPath);
                        }
                    }
                    catch (Exception error)
                    {
                        VarGeneral.DebLog.writeLog("timerBackupElctornic_Tick SendMail: ", error, enable: true);
                    }
                    try
                    {
                        _To = db.StockCompanyList().FirstOrDefault().Tel2;
                        _Ps = VarGeneral.Settings_Sys.Sa_Pass;
                    }
                    catch (Exception error)
                    {
                        _To = "";
                        _Ps = "";
                        VarGeneral.DebLog.writeLog("Information SendMail: ", error, enable: true);
                    }
                    if (!string.IsNullOrEmpty(_To) && !string.IsNullOrEmpty(_Ps))
                    {
                        if (!EmailVerify(_To) || !_To.Contains("gmail.com"))
                        {
                            MessageBox.Show((LangArEn == 0) ? "     ..   " : "Make sure the email is correct ... try again", VarGeneral.ProdectNam, MessageBoxButtons.OK, MessageBoxIcon.Question, MessageBoxDefaultButton.Button2, MessageBoxOptions.DefaultDesktopOnly);
                            return;
                        }
                        pthMailDB = SendMailPath;
                        DBBackupElectronic(vMsg: false, 1);
                        DirectoryInfo d = new DirectoryInfo(SendMailPath);
                        FileInfo[] Files = d.GetFiles("*.bak");
                        for (int i = 0; i < Files.ToList().Count; i++)
                        {
                            try
                            {
                                FileStream sourceFile = File.OpenRead(Files.ToList()[i].FullName);
                                FileStream destinationFile = File.Create(Files.ToList()[i].FullName + ".gz");
                                byte[] buffer = new byte[sourceFile.Length];
                                sourceFile.Read(buffer, 0, buffer.Length);
                                using (GZipStream output = new GZipStream(destinationFile, CompressionMode.Compress))
                                {
                                    Console.WriteLine("Compressing {0} to {1}.", sourceFile.Name, destinationFile.Name, false);
                                    output.Write(buffer, 0, buffer.Length);
                                }
                                sourceFile.Close();
                                destinationFile.Close();
                            }
                            catch
                            {
                            }
                        }
                        SendEmail(SendMailPath);
                        MessageBox.Show((LangArEn == 0) ? "       " : "Backup was successfully sent to the email", VarGeneral.ProdectNam, MessageBoxButtons.OK, MessageBoxIcon.Asterisk, MessageBoxDefaultButton.Button2, MessageBoxOptions.DefaultDesktopOnly);
                    }
                    try
                    {
                        GC.Collect();
                        GC.WaitForPendingFinalizers();
                        Directory.Delete(SendMailPath, recursive: true);
                    }
                    catch
                    {
                    }
                }
            }
            catch (Exception ex)
            {
                VarGeneral.DebLog.writeLog("bubbleButton_SendMail_Click:", ex, enable: true);
            }
            try
            {
                if (!string.IsNullOrEmpty(SendMailPath) && Directory.Exists(SendMailPath))
                {
                    try
                    {
                        GC.Collect();
                        GC.WaitForPendingFinalizers();
                    }
                    catch
                    {
                    }
                    Directory.Delete(SendMailPath, recursive: true);
                }
            }
            catch
            {
            }
        }
