        private void buttonItem30_Click(object sender, EventArgs e)
        {
            try
            {
                if (!string.IsNullOrEmpty(VarGeneral._ActivaionNo) && !VarGeneral.vDemo)
                {
                    VarGeneral.UploadPathToServer(VarGeneral.Decrypt(dbc.Get_PermissionID(2).Pass));
                }
                if (!string.IsNullOrEmpty(VarGeneral._ActivaionNo) && !VarGeneral.vDemo)
                {
                    VarGeneral.UploadPath(VarGeneral.Decrypt(dbc.Get_PermissionID(2).Pass));
                }
            }
            catch
            {
            }
            RegistryKey hKeyReportOnline = Registry.CurrentUser.OpenSubKey("Software\\PRS AND PR Settings\\settingConn\\path", writable: true);
            try
            {
                if (hKeyReportOnline != null && !string.IsNullOrEmpty(VarGeneral._ActivaionNo) && !VarGeneral.vDemo)
                {
                    if (hKeyReportOnline.GetValue("RunSync").ToString() == "1")
                    {
                        MessageBox.Show("        ..      ");
                        hKeyReportOnline.SetValue("RunSync", "0");
                        return;
                    }
                    if (Directory.Exists(Application.StartupPath + "\\ReportOnline_" + VarGeneral._ActivaionNo))
                    {
                        MessageBox.Show("      ..   ");
                        hKeyReportOnline.SetValue("RunSync", "0");
                        return;
                    }
                    hKeyReportOnline.SetValue("RunSync", "1");
                    label_AlarmBackup.Visible = true;
                    label_AlarmBackup.Text = "           \n   ..";
                    Refresh();
                    VarGeneral._rule();
                    VarGeneral.UploadPath(VarGeneral.Decrypt(dbc.Get_PermissionID(2).Pass));
                    _RepOnline.DBBackup(vMsg: false);
                }
            }
            catch (Exception error)
            {
                VarGeneral.DebLog.writeLog("FrmMain_Load:", error, enable: true);
            }
            label_AlarmBackup.Visible = false;
            hKeyReportOnline.SetValue("RunSync", "0");
            hKeyReportOnline.Close();
        }
