        private void buttonItem_BankOp_Click(object sender, EventArgs e)
        {
            if (dbc.Get_PermissionID(VarGeneral.UserID).UserPointTyp.Value == 1)
            {
                return;
            }
            try
            {
                RepShow _RepShow = new RepShow();
                _RepShow.Tables = "T_BankPeaper LEFT OUTER JOIN T_AccDef ON T_BankPeaper.CustAcc = T_AccDef.AccDef_No AND T_BankPeaper.BankAcc = T_AccDef.AccDef_No AND T_BankPeaper.BranchAcc = T_AccDef.AccDef_No LEFT OUTER JOIN T_SYSSETTING ON T_BankPeaper.CompanyID = T_SYSSETTING.SYSSETTING_ID LEFT OUTER JOIN T_INVSETTING ON T_BankPeaper.gdTyp = T_INVSETTING.InvID   ";
                string Fields = "";
                Fields = ((LangArEn != 0) ? " T_BankPeaper.PageNo, T_BankPeaper.PageDatePay, T_BankPeaper.Amount, T_BankPeaper.PageDate, T_BankPeaper.PayState,(select Eng_Des from T_AccDef as x  where x.AccDef_No = T_BankPeaper.CustAcc ) as CusVenNm,(select Eng_Des from T_AccDef as x  where x.AccDef_No = T_BankPeaper.BankAcc ) as AccDefNmBank,(select Eng_Des from T_AccDef as x  where x.AccDef_No = T_BankPeaper.BranchAcc ) as AccDefNmBr, T_SYSSETTING.LogImg,T_INVSETTING.InvNamE as InvNm ,case when vTyp = 1 then 'Peaper' else 'Draft' end as vTyp,T_INVSETTING.InvID,gdID,ID, PayState" : " T_BankPeaper.PageNo, T_BankPeaper.PageDatePay, T_BankPeaper.Amount, T_BankPeaper.PageDate, T_BankPeaper.PayState,(select Arb_Des from T_AccDef as x  where x.AccDef_No = T_BankPeaper.CustAcc ) as CusVenNm,(select Arb_Des from T_AccDef as x  where x.AccDef_No = T_BankPeaper.BankAcc ) as AccDefNmBank,(select Arb_Des from T_AccDef as x  where x.AccDef_No = T_BankPeaper.BranchAcc ) as AccDefNmBr, T_SYSSETTING.LogImg,T_INVSETTING.InvNamA as InvNm ,case when vTyp = 1 then '' else '' end as vTyp,T_INVSETTING.InvID,gdID,ID, PayState");
                _RepShow.Rule = " where gdTyp = 25 or gdTyp = 26 ";
                if (!string.IsNullOrEmpty(Fields))
                {
                    _RepShow.Fields = Fields;
                    try
                    {
                        _RepShow = _RepShow.Save();
                        VarGeneral.RepData = _RepShow.RepData;
                    }
                    catch (Exception ex2)
                    {
                        MessageBox.Show(ex2.Message, VarGeneral.ProdectNam, MessageBoxButtons.OK, MessageBoxIcon.Question, MessageBoxDefaultButton.Button2, MessageBoxOptions.DefaultDesktopOnly);
                    }
                }
                if (VarGeneral.RepData.Tables[0].Rows.Count == 0)
                {
                    MessageBox.Show((LangArEn == 0) ? " ..       " : "Sorry .. there is no data to display in the report ", VarGeneral.ProdectNam, MessageBoxButtons.OK, MessageBoxIcon.Asterisk, MessageBoxDefaultButton.Button2, MessageBoxOptions.DefaultDesktopOnly);
                    return;
                }
                try
                {
                    FrmReportsViewer frm = new FrmReportsViewer();
                    frm.Tag = LangArEn;
                    VarGeneral.InvTyp = 25;
                    frm.Repvalue = "RepBankPeaperList";
                    VarGeneral.vTitle = buttonItem_BankOp.Text;
                    frm.TopMost = true;
                    //frm.MdiParent = this;
                    //childmin();
                    frm.ShowDialog();
                }
                catch (Exception error)
                {
                    VarGeneral.DebLog.writeLog("button_ShowRep_Click:", error, enable: true);
                    MessageBox.Show(error.Message, VarGeneral.ProdectNam, MessageBoxButtons.OK, MessageBoxIcon.Question, MessageBoxDefaultButton.Button2, MessageBoxOptions.DefaultDesktopOnly);
                }
            }
            catch (Exception ex2)
            {
                MessageBox.Show(ex2.Message, VarGeneral.ProdectNam, MessageBoxButtons.OK, MessageBoxIcon.Question, MessageBoxDefaultButton.Button2, MessageBoxOptions.DefaultDesktopOnly);
            }
        }
