                    {
                        //_Room.TileStyle.BackColor = txtREmpty.BackColor;
                        //_Room.TileStyle.BackColor2 = txtREmpty.BackColor;
                        //_Room.TitleTextColor = txtREmpty.ForeColor;
                    }
                    else if (db.StockRoom(int.Parse(_Room.TitleText)).state.Value == 2)
