        List<List<T_CATEGORY>> CatPagesLists = new List<List<T_CATEGORY>>();
        void setpage()
        {
            CatSuperGrid.PrimaryGrid.Rows.Clear();
            GridRow cc = new GridRow();
            cc.RowHeight = CatSuperGrid.Height - 1;
            ;
            for (int j = 0; j < catPageSize; j++)
            {
                cc.Cells.Add(new GridCell(string.Empty));
                cc.Cells.LastOrDefault().EditorType = typeof(GridButtonXEditControl);
                if (base.WindowState == FormWindowState.Normal)
                {
                    cc.CellStyles.Default.Font = new Font("Tahoma", 6f, FontStyle.Bold);
                }
            }
            CatSuperGrid.PrimaryGrid.Rows.Add(cc); List<T_CATEGORY> dt = CatPagesLists[CatPagePosition];
            GridRow rowCell = (GridRow)CatSuperGrid.PrimaryGrid.Rows[0];
            int iicnt = 0;
            foreach (GridCell cell in rowCell.Cells)
            {
                try
                {
                    cell.CellStyles.Default.Image = null;
                }
                catch
                {
                }
                if (iicnt < dt.Count)
                {
                    try
                    {
                        {
                            cell.CellStyles.Default.AllowWrap = Tbool.True;
                            cell.Value = ((LangArEn == 0) ? dt[iicnt].Arb_Des : dt[iicnt].Eng_Des);
                            cell.Tag = dt[iicnt].CAT_ID;
                            iicnt++;
                        }
                    }
                    catch
                    {
                        iicnt++;
                    }
                }
                else
                {
                    cell.Visible = false;
                }
            }
            Refresh();
        }
        private void FillCat()
        {
            try
            {
                Stock_DataDataContext dbc = new Stock_DataDataContext(VarGeneral.BranchCS);
                FillCombo();
                try
                {
                    itemContainer_Cat.SubItems.Clear();
                    CatList = dbc.FillCat_2(string.Empty).ToList();
                    try
                    {
                        CatList = CatList.OrderBy((T_CATEGORY c) => int.Parse(c.CAT_No)).ToList();
                    }
                    catch
                    {
                    }
                    //      Program.min();
                    catPageSize = CatSuperGrid.Width / 120;
                    //catPageSize--;
                    CatPagePosition = 0;
                    catNoOFpages = (CatList.Count / catPageSize);
                    catNoOFpages++;
                    CatPagesLists.Clear();
                    int ind = 0, page = 0;
                    CatPagesLists.Add(new List<T_CATEGORY>());
                    T_CATEGORY ff = new T_CATEGORY();
                    ff.Arb_Des = ((LangArEn == 0) ? "" : "ALL");
                    ff.Arb_Des = ((0 == 0) ? "" : "ALL");
                    ff.Eng_Des = ((1 == 0) ? "" : "ALL");
                    ff.CAT_ID = 0;
                    CatPagesLists[page].Add(ff);
                    for (int js = 0; js < CatList.Count; js++)
                    {
                        if (ind == catPageSize - 1)
                        {
                            ind = 0;
                            page++;
                            CatPagesLists.Add(new List<T_CATEGORY>());
                            T_CATEGORY fxf = new T_CATEGORY();
                            fxf.Arb_Des = ((0 == 0) ? "" : "ALL");
                            fxf.Eng_Des = ((1 == 0) ? "" : "ALL");
                            fxf.CAT_ID = 0;
                            CatPagesLists[page].Add(fxf);
                        }
                        CatPagesLists[page].Add(CatList[js]);
                        ind++;
                    }
                    CatSuperGrid.PrimaryGrid.Columns.Clear();
                    for (int i = 0; i < catPageSize; i++)
                    {
                        GridColumn c = new GridColumn();
                        c.Width = 120;
                        CatSuperGrid.PrimaryGrid.Columns.Add(c);
                    }
                    setpage();
                    Refresh();
                    ItemsMainSetting();
                }
                catch (Exception error2)
                {
                    VarGeneral.DebLog.writeLog("FillCat:", error2, enable: true);
                    ItemsMainSetting();
                    Refresh();
