            }
            else if (e.KeyCode == Keys.F9)
            {
                if (button_openCasheir.Enabled)
                {
                    button_openCasheir_Click(sender, e);
                }
            }
            else if (e.KeyCode == Keys.Escape)
            {
                if (State == FormState.Saved)
                {
                    Close();
                    return;
                }
                if (State != FormState.New)
                {
                    textBox_ID_TextChanged(sender, e);
