                frm.TopMost = true;
                frm.ShowDialog();
                label_Pay.Visible = false;
                vRemming = 0.0;
                RepShow _RepShow = new RepShow();
                _RepShow.Tables = " T_InvHed Left Join   T_GDHEAD On T_InvHed.InvHed_ID = T_GDHEAD.BName Left Join  T_INVSETTING On T_InvHed.InvTyp = T_INVSETTING.InvID Left Join  T_Curency On T_InvHed.CurTyp = T_Curency.Curency_ID Left Join  T_CstTbl On T_InvHed.InvCstNo = T_CstTbl.Cst_ID Left Join  T_Mndob on T_InvHed.MndNo = T_Mndob.Mnd_Id Left Join  T_SYSSETTING ON T_InvHed.CompanyID = T_SYSSETTING.SYSSETTING_ID";
                string Fields = "";
                string _GdInv = "";
                _GdInv = "(CreditPay - case when (select sum(gdTot) from T_GDHEAD where T_GDHEAD.BName=T_INVHED.InvHed_ID and gdLok =0 and gdTyp =12) is null then 0 else (select sum(gdTot) from T_GDHEAD where T_GDHEAD.BName=T_INVHED.InvHed_ID and gdLok =0 and gdTyp =12) end) as Remming";
                Fields = ((LangArEn != 0) ? (" distinct T_InvHed.InvNo,T_INVSETTING.InvNamE as InvTypNm,T_InvHed.GDat,T_InvHed.HDat,T_InvHed.InvCash ,T_CstTbl.Eng_Des as CostCenteNm,T_Mndob.Eng_Des as MndNm,T_InvHed.CashPayLocCur,T_InvHed.NetworkPayLocCur,T_InvHed.CreditPayLocCur,T_InvHed.InvNetLocCur,(Round(T_InvHed.InvCost," + (VarGeneral.TString.ChkStatShow(VarGeneral.Settings_Sys.Seting, 49) ? 3 : 2) + ")) as  InvCost,(Round(T_InvHed.InvNetLocCur - T_InvHed.InvCost," + (VarGeneral.TString.ChkStatShow(VarGeneral.Settings_Sys.Seting, 49) ? 3 : 2) + ")) as InvProfit,T_InvHed.GadeNo,T_InvHed.CusVenNo,T_InvHed.CusVenNm,T_InvHed.Remark,T_SYSSETTING.LogImg ") : (" distinct T_InvHed.InvNo, T_INVSETTING.InvNamA as InvTypNm,T_InvHed.GDat,T_InvHed.HDat,T_InvHed.InvCash ,T_CstTbl.Arb_Des as CostCenteNm, T_Mndob.Arb_Des as MndNm,T_InvHed.CashPayLocCur,T_InvHed.NetworkPayLocCur,T_InvHed.CreditPayLocCur,T_InvHed.InvNetLocCur,(Round(T_InvHed.InvCost," + (VarGeneral.TString.ChkStatShow(VarGeneral.Settings_Sys.Seting, 49) ? 3 : 2) + ")) as  InvCost,(Round(T_InvHed.InvNetLocCur - T_InvHed.InvCost," + (VarGeneral.TString.ChkStatShow(VarGeneral.Settings_Sys.Seting, 49) ? 3 : 2) + ")) as InvProfit, T_InvHed.GadeNo ,T_InvHed.CusVenNo,T_InvHed.CusVenNm,T_SYSSETTING.LogImg, " + _GdInv));
                _RepShow.Rule = "Where T_InvHed.InvCashPay =1 and T_InvHed.CreditPay > 0  and T_InvHed.InvTyp = 1 and InvHed_ID=" + data_this.InvHed_ID;
                _RepShow.Fields = Fields;
                try
                {
                    _RepShow = _RepShow.Save();
                    VarGeneral.RepData = _RepShow.RepData;
                }
                catch (Exception ex)
                {
                    MessageBox.Show(ex.Message);
                }
                if (VarGeneral.RepData.Tables[0].Rows.Count > 0)
                {
                    try
                    {
                        vRemming = double.Parse(VarGeneral.TString.TEmpty(VarGeneral.RepData.Tables[0].Rows[0]["Remming"].ToString()));
