            columns_Names_visible2.Add("GDat", new ColumnDictinary(" ", "Date Gregorian", ifDefault: true, ""));
            columns_Names_visible2.Add("InvTot", new ColumnDictinary(" ", "Invoice Total", ifDefault: false, ""));
            columns_Names_visible2.Add("InvNet", new ColumnDictinary(" ", "Invoice Net", ifDefault: true, ""));
            columns_Names_visible2.Add("InvQty", new ColumnDictinary(" ", "Quantity Total", ifDefault: true, ""));
            columns_Names_visible2.Add("InvDisPrs", new ColumnDictinary(" ", "Discount Percentage", ifDefault: false, ""));
            columns_Names_visible2.Add("InvDisVal", new ColumnDictinary(" ", "Discount value", ifDefault: true, ""));
            columns_Names_visible2.Add("Remark", new ColumnDictinary("", "Note", ifDefault: true, ""));
            FrmSearch frm = new FrmSearch();
            frm.Tag = LangArEn;
            ICollection<KeyValuePair<string, ColumnDictinary>> animalsAsCollection = columns_Names_visible2;
            foreach (KeyValuePair<string, ColumnDictinary> kvp in animalsAsCollection)
            {
                frm.columns_Names_visible.Add(kvp.Key, new FrmSearch.SColumnDictinary(kvp.Value.AText, kvp.Value.EText, kvp.Value.IfDefault, ""));
            }
            VarGeneral.SFrmTyp = "T_InvSalReturn";
            frm.TopMost = true;
            frm.ShowDialog();
            if (!(frm.SerachNo != ""))
            {
                return;
            }
            try
            {
                T_INVHED newData = dbReturn.StockInvHead(VarGeneral.InvTypRt, frm.Serach_No);
                if (newData != null || !string.IsNullOrEmpty(newData.InvNo))
                {
                    CmbInvSide.SelectedIndexChanged -= CmbInvSide_SelectedIndexChanged;
                    State = FormState.Saved;
                    Clear();
                    OfferPrice = true;
                    DataThisRe = newData;
                    CmbInvSide.SelectedIndexChanged += CmbInvSide_SelectedIndexChanged;
                    if (VarGeneral._IsPOS)
                    {
                        AutoGaidAcc_POS();
                    }
                    else
                    {
                        AutoGaidAcc();
                    }
                }
                OfferPrice = false;
                dbInstanceReturn = null;
            }
            catch (Exception error)
            {
                OfferPrice = false;
                VarGeneral.DebLog.writeLog("ButReturn_Click:", error, enable: true);
                MessageBox.Show(error.Message);
            }
