                                                                 @ItmTax FLOAT =NULL,
                                                                 @OfferTyp INT =NULL,
																 @CInvType INT =NULL
                                                          )
                                                          AS
                                                          BEGIN
                                                          INSERT INTO T_INVDET(
                                                                 InvNo,
                                                                 InvId,
                                                                 InvSer,
                                                                 ItmNo,
                                                                 Cost,
                                                                 Qty,
                                                                 ItmDes,
                                                                 ItmUnt,
                                                                 ItmDesE,
                                                                 ItmUntE,
                                                                 ItmUntPak,
                                                                 StoreNo,
                                                                 Price,
                                                                 Amount,
                                                                 RealQty,
                                                                 itmInvDsc,
                                                                 DatExper,
                                                                 ItmDis,
                                                                 ItmTyp,
                                                                 ItmIndex,
                                                                 ItmWight,
                                                                 ItmWight_T,
                                                                 ItmAddCost,
                                                                 RunCod,
                                                                 LineDetails,
                                                                 Serial_Key,
                                                                 ItmTax,
                                                                OfferTyp ,
																CInvType
                                                          )
                                                          VALUES
                                                          (
                                                                 
                                                                @InvNo,
                                                                @InvId,
                                                                @InvSer,
                                                                @ItmNo,
                                                                @Cost,
                                                                @Qty,
                                                                @ItmDes,
                                                                @ItmUnt,
                                                                @ItmDesE,
                                                                @ItmUntE,
                                                                @ItmUntPak,
                                                                @StoreNo,
                                                                @Price,
                                                                @Amount,
                                                                @RealQty,
                                                                @itmInvDsc,
                                                                @DatExper,
                                                                @ItmDis,
                                                                @ItmTyp,
                                                                @ItmIndex,
                                                                @ItmWight,
                                                                @ItmWight_T,
                                                                @ItmAddCost,
                                                                @RunCod,
                                                                @LineDetails,
                                                                @Serial_Key,
                                                                @ItmTax,
                                                                @OfferTyp,
																@CInvType
                                                          )
                                                          SELECT @InvDet_ID = SCOPE_IDENTITY()
                                                            declare @ItemCount int  
                                                            declare @InvTyp int
                                                            declare @MndID int
                                                            declare @CusVenNo varchar(30)
                                                            declare @ItemCountMnd int 
                                                            declare @ItemCountCust int 
