                                                            select @InvTyp = InvTyp from T_INVHED where InvHed_ID = @InvId;
                                                            if((@InvTyp != -101 AND @InvTyp != 7 and @InvTyp != 8 and @InvTyp != 9 and @InvTyp != 21) and @ItmTyp <> 3)
                                                            begin
			                                                          if(@ItmTyp <> 2)
			                                                          begin
		                                                                    Update T_Items SET OpenQty = OpenQty+@RealQty WHERE Itm_No = @ItmNo;
			                                                                if(@ItemCount > 0)
