																	                      END
															                    END
														                      else
									                                            begin
															                             select @ItemCountCust = Count(*) from T_StoreMnd where itmNo = @ItmNo and storeNo =@StoreNo and CusVenNo = @CusVenNo;
																	                      if(@ItemCountCust > 0)
