																	                      END
															                    END
				                                                          END	
			                                                          if(@InvTyp = 20)
				                                                          begin
				                                                              select @MndID = MndNo from T_INVHED where InvHed_ID = @InvId;
														                      select @CusVenNo = CusVenNo from T_INVHED where InvHed_ID = @InvId;
